mapper.insert:
        INSERT INTO pd_activity_profile(
			user_id,tags,position,media_paths,media_type,longitude,latitude,
			geohash,status,like_num,heat_value,comment_num,	enshrine_num,description,
			create_time,modify_time,area_code,title , thumbnail_url
		)
        VALUES(
       		#{userId} , #{tags} , #{position} , #{mediaPaths} , #{mediaType} , #{longitude} , #{latitude} , #{geohash} , 'normal' , 0 , 1 ,
       		#{commentNum} , #{enshrineNum} , #{description} , NOW() , NOW(), #{areaCode}, #{title}, #{thumbnailUrl}
        )

mapper.update:
 	UPDATE 
 		pd_activity_profile
 	SET
 		modify_time = now() 
 		, issuer_id = #{issuerId} 
			, tags = #{tags} 
			, position = #{position} 
			
			, media_type = #{mediaType} 
			, media_paths = #{mediaPaths} 
			, description_voice_path = #{descriptionVoicePath} 
			, description_type = #{descriptionType} 
			, description_text = #{descriptionText} 
    		, like_num = #{likeNum} 
    		, heat_value = #{heatValue} 
    		, comment_num = #{commentNum} 
    		, enshrine_num = #{enshrineNum} 
			, longitude = #{longitude} 
			, latitude = #{latitude} 
			, geohash = #{geohash} 
			, status = #{status} 
		WHERE
			id = #{id}

select.where:
    		1 = 1
	    	AND issuer_id = #{issuerId} 
   			AND position LIKE ('%' || #{position, jdbcType=VARCHAR} || '%') 
   			AND short_message LIKE ('%' || #{descriptionText, jdbcType=VARCHAR} || '%') 
   			
   				earth_box(ll_to_earth(#{longitude}, #{latitude}), #{range}) @> ll_to_earth(pd_activity_profile.latitude, pd_activity_profile.longitude);

mapper.select:
SELECT * , CASE (SELECT count(1) FROM pd_heat_info WHERE trigger_user_id = #{loginedUserId} AND activity_id = a.id) WHEN 0 THEN 2 ELSE 1 END AS is_heat , CASE (SELECT count(1) FROM pd_enshrine_info WHERE collector_id = #{loginedUserId} AND activity_id = a.id) WHEN 0 THEN 2 ELSE 1 END AS is_enshrine , (SELECT count(1) FROM pd_follow_link WHERE follower_id = #{loginedUserId}) follower_count , (SELECT count(1) FROM pd_follow_link WHERE be_follower_id = #{loginedUserId}) be_follower_count FROM pd_activity_profile a
1 = 1 AND issuer_id = #{issuerId} AND position LIKE ('%' || #{position, jdbcType=VARCHAR} || '%') AND short_message LIKE ('%' || #{descriptionText, jdbcType=VARCHAR} || '%')
earth_box(ll_to_earth(#{longitude}, #{latitude}), #{range}) @> ll_to_earth(pd_activity_profile.latitude, pd_activity_profile.longitude);

SELECT ap.* , up.head_portrait , CASE WHEN heat.id > 0 THEN 1 ELSE 0 END heated FROM pd_activity_profile ap LEFT JOIN pd_user_profile up ON up.id = ap.user_id LEFT JOIN (select * from pd_heat_info phi where phi.user_id = #{userId} ) heat on heat.activity_id = ap.id WHERE earth_box(ll_to_earth(cast(#{latitude} as float4),cast(#{longitude} as float4)),1000) @> ll_to_earth(ap.latitude, ap.longitude) AND ap.id > #{lastItemId}
and
ap.id != #{item.activityId}
LIMIT #{pageSize} OFFSET 0;
