mapper.update:
UPDATE 
	pd_user_profile
SET
	modify_time = CURRENT_TIMESTAMP
	, username = #{username} 
	, name = #{name} 
	, nickname = #{nickname} 
	, gender = #{gender} 
	, location = #{location} 
	, signature = #{signature} 
	, email = #{email} 
	, wechat = #{wechat} 
	, sina_weibo = #{sinaWeibo} 
	, twiter = #{twiter} 
	, facebook = #{facebook} 
	, linkedin = #{linkedin} 
	, headPortrait = #{headPortrait} 
WHERE
	phone_number = #{phoneNumber}
